import { Component } from '@angular/core';

@Component({
  selector: 'app-boite',
  templateUrl: './boite.component.html',
  styleUrls: ['./boite.component.css'],
})
export class BoiteComponent {
  // @Input()
  // tirelire1 : number = 1000;
  // @Input()
  // tirelire2 : number = 1000;
  // @Input()
  // tirelire3 : number = 1000;

  // @Output()
  // argentdepense: EventEmitter<number> = new EventEmitter<number>();
  // procesSpendMoney(){
  //   this.tirelire1.emit(this.numero)

  tirelire1: number = 0;
  tirelire2: number = 0;
  tirelire3: number = 0;

  ajouter100Fr() {
    const random = Math.floor(Math.random() * 3) + 1;
    if (random === 1) {
      this.tirelire1 += 100;
    } else if (random === 2) {
      this.tirelire2 += 100;
    } else {
      this.tirelire3 += 100;
    }
  }

  utiliserTirelire(tirelire: number) {
    switch (tirelire) {
      case 1:
        this.tirelire1 -= this.tirelire1;
        break;
      case 2:
        this.tirelire2 -= this.tirelire2;
        break;
      case 3:
        this.tirelire3 -= this.tirelire3;
        break;
    }
  }
}
<div class="boite">
  <div class="tirelire">
    <!-- <app-boite [tirelire]="1" [valeur]="tirelire[0]" (spendMoney)="$event"></app-boite> -->

    Tirelire 1 : {{ tirelire1 }}
    <button (click)="utiliserTirelire(1)">
      Utiliser les {{ tirelire1 }} Fr
    </button>
  </div>
  <div class="tirelire">
    Tirelire 2 : {{ tirelire2 }}
    <button (click)="utiliserTirelire(2)">
      Utiliser les {{ tirelire2 }} Fr
    </button>
  </div>
  <div class="tirelire">
    Tirelire 3 : {{ tirelire3 }}
    <button (click)="utiliserTirelire(3)">
      Utiliser les {{ tirelire3 }} Fr
    </button>
  </div>
  <button class="ajouter" (click)="ajouter100Fr()">Ajouter 100 Fr</button>
</div>
